//显示当前时间
window.setInterval("realSysTime(clock)",1500); 
function realSysTime(clock){
    var now=new Date();            //创建Date对象
    var year=now.getFullYear();    //获取年份
    var month=now.getMonth();    //获取月份
    var date=now.getDate();        //获取日期
    var day=now.getDay();        //获取星期
    var hour=now.getHours();    //获取小时
    var minu=now.getMinutes();    //获取分钟
    var sec=now.getSeconds();    //获取秒钟
    month=month+1;
    var arr_week=new Array("星期日","星期一","星期二","星期三","星期四","星期五","星期六");
    var week=arr_week[day];  //获取中文的星期
    var time="<div class=time1>"+((hour<10)?('0'+hour):hour)+"∶"+((minu<10)?('0'+minu):minu)+"</div><div class=time2>"+((hour<10)?('0'+hour):hour)+":"+((minu<10)?('0'+minu):minu)+"</div><div class=time3>"+(month)+"月"+(date)+"日 "+(week)+"</div>";    //组合系统时间
    clock.innerHTML=time;    //显示系统时间
}


